package javabasics;
/*
 * 4.Write a program to print even numbers from 1 to 50 using for loop.
 */
public class TestCase4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<=50;i++)
		{
			if(i%2==0)
			{
				System.out.println(i);
			}
		}
	}
}
